#!/usr/bin/env node

// External Dependencies
var argv = require('optimist').argv;

// Internal Dependencies
var jsb = require('../lib');

// Shortcuts

// Module vars
var min = true;

// Default parameters

if (argv.min === 'true') {
    min = true;
}
if (argv.min === 'false') {
    min = false;
}

jsb.build({
    root: process.cwd(),
    input: argv.input,
    output: argv.output,
    min: min
});