var fs = require('fs');

exports.init = function (request, callback) {
	callback({
		filename: request.filename
	});
};

exports.getContent = function (request, callback) {
	callback(fs.readFileSync(request.filename) + '');
};

exports.saveFile = function (request, callback) {
	try {
		fs.writeFileSync(request.filename, request.content);
	} catch(err) {
		
	} finally {
		callback();
	}
};